var $ = require("jquery");
var signal = require('signal').createSignal();








var TopController = function(){



    this.init = function(){


        bindEvent()
    }


    function bindEvent(){
        $(".mark-all-as-read-btn").on("click", function(){
            console.log("mark-all-as-read-btn click event");


            signal.publish('mark-all-as-read')();
        })
    }


    return this;

}



exports.TopController= TopController;

